import {createSelector} from 'reselect';

const COLLECTION_ID_MAP = {
    hats: 1,
    sneakers: 2, 
    jackets: 3, 
    womens: 4,
    mens: 5
}

const selectShop = state => state.shop;

function selectId(collectionUrlParam){
    console.log(collectionUrlParam)
    console.log("selected ID", COLLECTION_ID_MAP[collectionUrlParam])

    return COLLECTION_ID_MAP[collectionUrlParam]
}

export const selectCollections = createSelector(
    [selectShop],
    shop => shop.collections
);

export const selectCollectionById = collectionUrlParam => createSelector(
    [selectCollections],
    collections => collections.find(collection => collection.id === selectId(collectionUrlParam))
);
