import { connect } from 'react-redux';
import { selectCurrentUser } from '../../redux/user/user.selectors';
import { createStructuredSelector } from 'reselect';
import StripeCheckout from 'react-stripe-checkout'

class StripeCheckoutButton extends React.Component { 

    render(){ 
        const priceForStripe = price * 100;
        const publishableKey = 'pk_test_A0v5iG1WXz4n3RCfV8uCE2gD00CpAFKE9n';
        // const { currentuser}
        console.log("THE CURRENT USER IS", this.props.currentUser)

        const onToken = token => {
            console.log(token)
            alert('Payment Successful')
        }
        return (
            <StripeCheckout
                label='Pay Now'
                name="CRWN Clothing Ltd."
                billingAddress
                shippingAddress
                image='https://svgshare.com/i/CUz.svg'
                description={`Your total is $${price}`}
                amount={priceForStripe}
                panelLabel='Pay now'
                token={onToken}
                stripeKey={publishableKey}>
                <button className="btn btn-primary">
                    Pay Now 
                </button>
            </StripeCheckout>
        );
    }
};

const mapStateToProps = createStructuredSelector({
    currentUser: selectCurrentUser
});

export default connect(mapStateToProps)(StripeCheckoutButton);
